mapper.update:
UPDATE mei_take_out.setmeal
category_id = #{categoryId}, description = #{description}, image = #{image}, name = #{name}, price = #{price}, status = #{status},
WHERE id = #{id}

mapper.delete:
DELETE FROM mei_take_out.setmeal
WHERE id IN

    #{id}

mapper.select:
SELECT s.id, s.category_id, s.name, s.price, s.status, s.description, s.image, s.update_time, c.name AS categoryName FROM mei_take_out.setmeal s LEFT JOIN mei_take_out.category c ON s.category_id = c.id
AND s.category_id = #{categoryId}
AND s.name = CONCAT('%', #{name},'%')

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
